contracts/LoanPlatform.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.21;

import "./SiliquaCoin.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract LoanPlatform {
  using SafeERC20 for IERC20;

  SiliquaCoin public token;
  address public owner;
  uint256 public interestRate;
  uint256 public totalLoans;

  struct Loan {
    uint256 loanId;
    address borrower;
    uint256 amount;
    uint256 interest;
    uint256 totalAmount;
    uint256 repaidAmount;
    bool active;
  }

  mapping(address => Loan) public loans;

  event LoanCreated(
    address indexed borrower,
    uint256 indexed loanId,
    uint256 amount,
    uint256 totalAmount
  );
  event LoanRepaid(
    address indexed borrower,
    uint256 indexed loanId,
    uint256 repaidAmount
  );

  constructor(address _tokenAddress, uint256 _interestRate) {
    token = SiliquaCoin(_tokenAddress);
    owner = msg.sender;
    interestRate = _interestRate;
    totalLoans = 0;
  }

  modifier onlyOwner() {
    require(msg.sender == owner, "Only the owner can call this function");
    _;
  }

  function setInterestRate(uint256 _newInterestRate) external onlyOwner {
    interestRate = _newInterestRate;
  }

  function requestLoan(uint256 _amount) external {
    require(!loans[msg.sender].active, "Active loan exists");

    uint256 interest = (_amount * interestRate) / 100;
    uint256 totalAmount = _amount + interest;

    IERC20(token).safeTransferFrom(owner, msg.sender, _amount);

    uint256 loanId = uint256(
      keccak256(abi.encodePacked(msg.sender, block.timestamp, _amount))
    );

    loans[msg.sender] = Loan({
      loanId: loanId,
      borrower: msg.sender,
      amount: _amount,
      interest: interest,
      totalAmount: totalAmount,
      repaidAmount: 0,
      active: true
    });

    totalLoans += 1;

    emit LoanCreated(msg.sender, loanId, _amount, totalAmount);
  }

  function repayLoan(uint256 _amount) external {
    require(loans[msg.sender].active, "No active loan");
    require(
      _amount <= loans[msg.sender].totalAmount,
      "Invalid repayment amount"
    );

    IERC20(token).safeTransferFrom(msg.sender, owner, _amount);

    if (loans[msg.sender].amount != 0) {
      loans[msg.sender].amount -= _amount;
    }
    loans[msg.sender].repaidAmount += _amount;
    loans[msg.sender].totalAmount -= _amount;

    if (loans[msg.sender].totalAmount == 0) {
      loans[msg.sender].active = false;
      emit LoanRepaid(
        loans[msg.sender].borrower,
        loans[msg.sender].loanId,
        loans[msg.sender].repaidAmount
      );
    }
  }
}

contracts/SiliquaCoin.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.21;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract SiliquaCoin is IERC20 {
  address public owner;
  string public name;
  string public symbol;
  uint8 public decimals;
  uint256 public override totalSupply;

  mapping(address => uint256) private balances;
  mapping(address => mapping(address => uint256)) private allowances;

  event OwnershipTransferred(
    address indexed previousOwner,
    address indexed newOwner
  );

  constructor(
    string memory _name,
    string memory _symbol,
    uint8 _decimals,
    uint256 initialSupply
  ) {
    name = _name;
    symbol = _symbol;
    decimals = _decimals;
    totalSupply = initialSupply;
    balances[msg.sender] = initialSupply;
    owner = msg.sender;
    emit Transfer(address(0), msg.sender, initialSupply);
  }

  modifier onlyOwner() {
    require(msg.sender == owner, "Only the owner can call this function");
    _;
  }

  function transfer(
    address to,
    uint256 amount
  ) external override returns (bool) {
    require(balances[msg.sender] >= amount, "Insufficient balance");
    balances[msg.sender] -= amount;
    balances[to] += amount;
    emit Transfer(msg.sender, to, amount);
    return true;
  }

  function transferFrom(
    address from,
    address to,
    uint256 amount
  ) external override returns (bool) {
    require(amount <= balances[from], "Insufficient balance");
    require(amount <= allowances[from][msg.sender], "Insufficient allowance");
    balances[from] -= amount;
    balances[to] += amount;
    allowances[from][msg.sender] -= amount;
    emit Transfer(from, to, amount);
    return true;
  }

  function approve(
    address spender,
    uint256 amount
  ) external override returns (bool) {
    allowances[msg.sender][spender] = amount;
    emit Approval(msg.sender, spender, amount);
    return true;
  }

  function allowance(
    address tokenOwner,
    address spender
  ) external view override returns (uint256) {
    return allowances[tokenOwner][spender];
  }

  function balanceOf(address account) external view override returns (uint256) {
    return balances[account];
  }

  function burn(uint256 amount) external onlyOwner returns (bool) {
    require(amount > 0, "Invalid amount");
    require(amount <= balances[msg.sender], "Insufficient balance");
    balances[msg.sender] -= amount;
    totalSupply -= amount;
    emit Transfer(msg.sender, address(0), amount);
    return true;
  }

  function mint(address to, uint256 amount) external onlyOwner returns (bool) {
    totalSupply += amount;
    balances[to] += amount;
    emit Transfer(address(0), to, amount);
    return true;
  }

  function transferOwnership(address newOwner) external onlyOwner {
    require(newOwner != address(0), "Invalid owner address");
    emit OwnershipTransferred(owner, newOwner);
    owner = newOwner;
  }
}

